<?error_reporting(E_ALL);ini_set('error_reporting', E_ALL);$TABLE = $_POST['TABLE'];$DELETE = urldecode($_POST['DELETE']);$CHAMP = urldecode($_POST['CHAMP']);$no_load = 1;include "ress/util.php";include "ress/db_mysql.php";include ("table_param_".$TABLE.".php");$colums = explode('|', $DELETE);$valls = explode('|', $CHAMP);$i= 0;$clause = "";foreach ($colums as $colum) {	$clause .= $colums[$i]." = '".($valls[$i])."' and ";	$i++;}$clause = substr($clause, 0, -4);$obj = new db_sql("	select 		*	from 		$TABLE	where		$clause");$obj->n();foreach($champs as $champ => $prop) {	$valeur = $obj->f($champ);		if($prop['format'] == 'number') {		$add = ' class = "resultat_list text-center" ';	} else {		$add = ' class = "resultat_list" ';	}	if($prop['format'] == 'liste'){		$req_list = new db_sql($champs[$champ]['liste_valeurs']);		while($req_list->n()) {			$liste_dd[$req_list->f('id')] = $req_list->f('lb');		}	}	echo "<td $add nowrap>";            	if($champs[$champ]['clee'] == 0){		if($prop['format'] == 'liste'){			if(!$liste_dd[$valeur]){				$lib_valeur = '!!!: '.$valeur;				$liste_dd = [$valeur=>$lib_valeur] + $liste_dd;			}			echo drop_down_droit('O',$prop['liste_valeurs'], $champ, "id", "lb", $valeur, "","O", "", " ");			$valeur = $liste_dd[$valeur];		} elseif($prop['format'] == 'texte_area'){			echo text_area('O', $champ, $valeur, 70, 2, 500);		} else {			echo champ_ouvert($champ, $valeur, 200, 30, $mandatory);								}	} else {		echo champ_cache($champ,$valeur);			if($prop['format'] == 'liste'){			$valeur = $liste_dd[$valeur];		}		echo $valeur;	}	echo "</td>";}echo champ_cache("ACTE","UPDATE");echo '<td class="resultat_list text-center">			<input type = "submit" value = "Enregistrer">	</td>';echo "<td class='resultat_list'>";echo "<a style='display:block' href='?ACTE=DEL&DELETE=".urlencode($DELETE)."&CHAMP=".urlencode($CHAMP)."'><img style = 'display:block; margin-left:auto; margin-right:auto;' class='t